import React, { Component } from 'react'
import axios from 'axios'
import '../../index.css';

class Counter extends Component {
    constructor(props) {
        super(props)
        this.state = {
            requests: []
        }
    }

     componentDidMount() {
         axios.get("http://localhost:3003/requests")
         .then(res => {
            const requests = res.data;
            const amount = res.data.length;
            this.setState = ({ requests, amount });
         })
}

    render() {

        return (
            <div className="counter">
                <h1>
                    Available Tasks: {this.state.amount}
                </h1>
            </div>
        )
        }
    }

export default Counter
