import React, { Component } from 'react';
import DummyContent from '../../Dummy';
import RequestForm from '../../RequestForm';
import { Accordion, AccordionItem } from 'react-light-accordion';
import 'react-light-accordion/demo/css/index.css';
import { AccordionTitle } from 'semantic-ui-react';

const AccordionMenu = () => {
    const {message, setMessage} = React.useState

    handleClick = () => {
      axios.delete('http://localhost:3003/logout', {withCredentials: true})
      .then(response => {
        this.props.handleLogout()
        this.props.history.push('/')
      })
      .catch(error => console.log(error))
    }
    return (
      <div className="DropMenu">
        <Accordion atomic={true}>

          <AccordionItem title="Create a Task ">
            <RequestForm />
          </AccordionItem>

          <AccordionItem title="Done for Now">
          <div>
       <Header/>
       <br></br>
       { 
        this.props.loggedInStatus ? 
        <Link to='/logout' onClick={this.handleClick}>Log Out</Link> : 
        null
      }
   </div>
          </AccordionItem>
            
        </Accordion>
      </div>
    );
  }

export default AccordionMenu
