class FulfilledButton extends Component {
    constructor(){
        super()
         this.state = {
          status: [],
          fulfilled: false 
    };

    this.toggleFulfilled=this.toggleFulfilled.bind(this)
    }

    toggleFulfilled = () => {
        const { fulfilled } = this.state;
        this.setState({fulfilled: !fulfilled});
    }

     handleSubmit = (event) => {
    event.preventDefault();
    this.toggleFulfilled();
    const {fulfilled} = this.state
    const { request, volunteer } = this.props
    let status = {
      volunteer_id: volunteer.id,
      request_id: request.id,
      fulfilled: fulfilled
    }
        axios.post("http://localhost:3003/requests", {status}, {withCredentials: true})
        .then(response => {
            if (response.data.status === 200) {
            //this.props.status(response.data)
            this.redirect('/')            
        }
    })        
        .catch(error => {
            console.log("request error", error);
        });
        event.preventDefault();
    }


    render(){
        return <button onClick={this.handleSubmit}>Fulfill Request{this.state.status}</button>
    }
}

export default FulfilledButton;
