import axios from "axios";
import React, { Component } from "react";

class Republish extends Component {
    constructor(){
        super()
        this.state = {
            fulfilled: true,
        };
        this.handleShow = this.handleShow.bind(this);
        this.handleHide = this.handleHide.bind(this);
    }

    handleShow() {
        this.setState=({fulfilled: false});
    }

    handleHide() {
        this.setState=({fulfilled: true});
    }

    handleSubmit = (event) => {
        const {fulfilled} = this.setState;
        const { request } = this.props;

        console.log(request);
        axios.patch(`http://localhost:3003/requests/${request.id}`, {fulfilled: false}, {withCredentials: true})
            .then(response => {
            console.log(response);
            if (response.data.status === 200) {
            console.log(response.data);
            // this.props.status(response.data)
            this.redirect('/')
            }
            })
            .catch(error => {
            console.log("request error", error);
            });
            }
            render() {
                if (fulfilled === true){
                    return (
                        <div className="hiddenList">
                            <button onHover={this.handleHide} />
                        </div>
                    )
                }
                else {
                return(
                    <button onClick={this.handleSubmit, this.handleShow}>Republish</button>
                )
            }
                }
            }
export default Republish;
