// export default FulfilledButton;

class FulfilledButton extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fulfilled: false
        };
    }

    handleChange = (event) => {
        const {value} = event.target
        this.setState({
            value 
        })
    };

    handleSubmit = (event) => {
        event.preventDefault()
        const {fulfilled} = this.state
        let fulfill = {
            fulfilled: fulfilled
        }

        axios.patch("http://localhost:3003/requests/:id(:fulfilled)", {fulfill}, 
        { withCredentials: true })
        .then(response => {
            this.props.requests(response.data)
        })
        .catch(error => {
            console.log("requests error", error)
        });
        event.preventDefault();
    }

    render(){
        return(
            <div className="fulfilledbtn">
                <form>
                    <h3>Fulfilled</h3>
                <input 
                    type="checkbox"
                    checked={this.handleChange}
                    onChange={this.handleSubmit}>

                </input>
                </form>
            </div>
        )
    }
}

export default FulfilledButton;
